    public List GetDados(){
    
List lista = new ArrayList(); 

//for (int i = 0; i < 10; i++) {
AuxiliarAtestadoMedico print =  new AuxiliarAtestadoMedico(); 
//print.setCodigo("");

print.setNome(txtNomePaciente.getText());
print.setCpf(txtCPF.getText());
print.setDas(txtDas.getText());
print.setAs(txtAs.getText());
print.setData(txtDataAtestado.getEditor().getText());
print.setNecessita(txtDias.getText());
print.setCid(txtCid.getText());
lista.add(print);   
return lista;    
}

